mapper.select:
SELECT id FROM tb_checkin
WHERE user_id = #{userId} AND date = CURRENT_DATE
AND create_time BETWEEN #{start} AND #{end}
LIMIT 1;

SELECT COUNT(*) AS count
FROM tb_checkin
WHERE user_id = #{userId}

SELECT COUNT(*) AS count
FROM tb_checkin
WHERE  user_id = #{userId} AND risk = #{risk}
